import React, { useState } from 'react';
import PhotoUpload from './PhotoUpload';

function Create_form({ onBlogCreate }) {
  const [formData, setFormData] = useState({
    blogName: '',
    // Добавьте другие поля по мере необходимости
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Вызывайте функцию onBlogCreate и передавайте данные блога
    onBlogCreate(formData);
    // Сбросьте состояние формы после отправки
    setFormData({
      blogName: '',
      // Сбросьте другие поля по мере необходимости
    });
  };

  return (
    <section className="blog">
      <div className="blog-header">
        {/* ... (оставьте остальной код без изменений) */}
      </div>
      <form className="blog-content" onSubmit={handleSubmit}>
        <input
          type="text"
          name="blogName"
          placeholder="Что хотите писать?"
          value={formData.blogName}
          onChange={handleInputChange}
        />
        <PhotoUpload />
        <button type="submit">Создать блог</button>
      </form>
    </section>
  );
}

export default Create_form;
