import React, { useState } from 'react';

function Blog_main({ blog, onLike, onDislike, onSave }) {
  const [isLiked, setIsLiked] = useState(false);
  const [isDisliked, setIsDisliked] = useState(false);
  const [isSaved, setIsSaved] = useState(false);

  const handleLikeClick = () => {
    setIsLiked(!isLiked);
    onLike(blog.id); // Передаем id блога, который лайкнули, родительскому компоненту
  };

  const handleDislikeClick = () => {
    setIsDisliked(!isDisliked);
    onDislike(blog.id); // Передаем id блога, который дизлайкнули, родительскому компоненту
  };

  const handleSaveClick = () => {
    setIsSaved(!isSaved);
    onSave(blog.id); // Передаем id блога, который сохранили, родительскому компоненту
  };

  if (!blog) {
    return null;
  }

  return (
    <section className="blog">
      {/* ... (остальной код без изменений) ... */}
      <div className="buttons">
        <button onClick={handleLikeClick}>
          <img src="/img/like.png" alt="Like" />
        </button>
        <button onClick={handleDislikeClick}>
          <img src="/img/dislike.png" alt="Dislike" />
        </button>
        <button onClick={handleSaveClick}>
          <img src={isSaved ? "/img/saved.png" : "/img/save.png"} alt="Save" />
        </button>
      </div>
    </section>
  );
}

export default Blog_main;
