import React, { useState } from 'react';
import Header_main from './components/Header_main'
import News_header from './components/MAIN/News_header';
import Blog_main from './components/MAIN/Blog_main';
import Navbar from './components/Navbar';
import Profil_page from './components/PROFIL/Profil_page';
import Blogers_page from './components/BLOGERS/Blogers_page';
import Main_page from './components/MAIN/Main_page';

function App() {
  // Используем состояние для отслеживания текущей страницы
  const [currentPage, setCurrentPage] = useState('blog'); // 'blog' по умолчанию

  // Функция для обновления текущей страницы
  const changePage = (page) => {
    setCurrentPage(page);
  };

  // Функция для рендеринга текущей страницы
  const renderCurrentPage = () => {
    if (currentPage === 'profil') {
      return <Profil_page />;
    } else if (currentPage === 'blogers') {
      return <Blogers_page />;
      
    } else {
      return <Main_page />;
    }
  };

  return (
    <body>
      <header>
        <Header_main />
      </header>
      <main className='Main'>
        <aside>
          <Navbar onPageChange={changePage} />
        </aside>
        <div class="content">
          {renderCurrentPage()}
        </div>
      </main>
    </body>
  );
}

export default App;
