import React, { useState } from 'react';
import Blog_main from "../MAIN/Blog_main";
import Create_form from "./Create_form";

function Myblog_page() {
  const [isCreateFormVisible, setCreateFormVisible] = useState(false);

  const handleCreateFormToggle = () => {
    setCreateFormVisible(!isCreateFormVisible);
  };

  return (
    <section className="myblog">
      <div className="news-header">
        <div>
          <img src="/img/logo.jpeg" />
          <h1>Мои Блоги</h1>
        </div>
        <button className='create-btn' onClick={handleCreateFormToggle}>
          <span>+</span>
        </button>
      </div>

      {isCreateFormVisible && <Create_form />}

      <Blog_main />
    </section>
  );
}

export default Myblog_page;
