import React from 'react';

// Компонент для представления одного пользователя
const UserCard = ({ user }) => {
  return (
    <div className="user-card">
      <img src={user.avatarUrl} alt={`${user.name} ${user.surname}`} />
      <h3>{`${user.name} ${user.surname}`}</h3>
      <p>Телефон: {user.phonenumber}</p>
      <p>Группа: {user.group}</p>
      {/* Другие данные пользователя */}
    </div>
  );
};

// Компонент для хранения и отображения списка пользователей
const UserList = ({ users }) => {
  return (
    <div className="user-list">
      <h2>Список пользователей</h2>
      {users.map((user, index) => (
        <UserCard key={index} user={user} />
      ))}
    </div>
  );
};

// Пример данных о пользователях
// Пример данных о пользователях
const users = [
  {
    name: 'Danial',
    surname: 'Abibulla',
    password: 'password1',
    phonenumber: '871234567',
    avatarUrl: '/img/logo.jpeg',
    group: 'IT2-2114'
  },
  {
    name: 'Batyrkhan',
    surname: 'Rukhanov',
    password: 'password2',
    phonenumber: '88712345678',
    avatarUrl: '/img/logo.jpeg',
    group: 'IT2-2114'
  },
  {
    name: 'Adilbek',
    surname: 'Adilzhan',
    password: 'password3',
    phonenumber: '887123456789',
    avatarUrl: '/img/logo.jpeg',
    group: 'IT2-2114'
  },
  {
    name: 'Batyrkhan',
    surname: 'Otynchiev',
    password: 'password4',
    phonenumber: '88712345689',
    avatarUrl: '/img/logo.jpeg',
    group: 'IT2-2114'
  },
];

// Главный компонент, использующий UserList
const App = () => {
  return (
    <div className="app">
      <UserList users={users} />
    </div>
  );
};

export default App;
