import React, { useState, useEffect } from 'react';
import Blog_main from "../MAIN/Blog_main";
import Create_form from "./Create_form";
import Blogs from './Blogs';

function Myblog_page() {
  // Загружаем блоги из Local Storage при первой загрузке страницы
  const [blogs, setBlogs] = useState(() => {
    const storedBlogs = localStorage.getItem('blogs');
    return storedBlogs ? JSON.parse(storedBlogs) : [];
  });

  // Сохраняем блоги в Local Storage при изменении
  useEffect(() => {
    localStorage.setItem('blogs', JSON.stringify(blogs));
  }, [blogs]);

  const handleBlogCreate = (newBlogData) => {
    // Добавляем новый блог в список
    setBlogs((prevBlogs) => [...prevBlogs, newBlogData]);
  
    // Проверяем, что блоги сохраняются в Local Storage
    const storedBlogs = localStorage.getItem('blogs');
    console.log(JSON.parse(storedBlogs));
  };
  

  return (
    <section className="myblog">
      <div className="news-header">
        <div>
          <img src="/img/logo.jpeg" />
          <h1>Мои Блоги</h1>
        </div>
      </div>
      <Create_form onBlogCreate={handleBlogCreate} />
      <Blog_main blogs={blogs} /> {/* Передаем блоги как пропс в Blog_main */}
      <Blogs/>
    </section>
  );
}

export default Myblog_page;
